  --popover-border: 220 15% 94%;

  --primary: 210 100% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 220 12% 92%;

  --secondary-foreground: 220 15% 15%;

  --muted: 220 10% 94%;

