import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Phone, ArrowLeft, Play, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { ThemeToggle } from "@/components/theme-toggle";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogFooter } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCampaignSchema, type Campaign } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Link, useLocation } from "wouter";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";

const formSchema = insertCampaignSchema.extend({
  name: z.string().min(1, "Campaign name is required"),
  description: z.string().optional(),
});

type FormData = z.infer<typeof formSchema>;

function CampaignCard({ campaign, onSelect }: { campaign: Campaign; onSelect: (id: string) => void }) {
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/campaigns/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: "Campaign deleted",
        description: "The campaign has been removed successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete campaign.",
        variant: "destructive",
      });
    },
  });

  const handleDelete = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (window.confirm("Are you sure you want to delete this campaign?")) {
      deleteMutation.mutate(campaign.id);
    }
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      draft: { variant: "outline" as const, color: "text-muted-foreground" },
      active: { variant: "default" as const, color: "text-primary-foreground" },
      completed: { variant: "secondary" as const, color: "" },
      paused: { variant: "outline" as const, color: "text-warning" },
    };

    const config = variants[status as keyof typeof variants] || variants.draft;

    return (
      <Badge variant={config.variant} className={config.color} data-testid={`badge-status-${campaign.id}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </Badge>
    );
  };

  return (
    <Card 
      className="hover-elevate active-elevate-2 cursor-pointer" 
      onClick={() => setLocation(`/campaigns/${campaign.id}`)}
      data-testid={`card-campaign-${campaign.id}`}
    >
      <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-4">
        <div className="flex-1 min-w-0">
          <CardTitle className="text-lg truncate" data-testid={`text-campaign-name-${campaign.id}`}>
            {campaign.name}
          </CardTitle>
          {campaign.description && (
            <CardDescription className="mt-1 line-clamp-2" data-testid={`text-campaign-desc-${campaign.id}`}>
              {campaign.description}
            </CardDescription>
          )}
        </div>
        <div className="flex items-center gap-2">
          {getStatusBadge(campaign.status)}
          <Button 
            size="icon" 
            variant="ghost" 
            onClick={handleDelete}
            data-testid={`button-delete-${campaign.id}`}
          >
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        <p className="text-sm text-muted-foreground" data-testid={`text-created-${campaign.id}`}>
          Created {new Date(campaign.createdAt).toLocaleDateString()}
        </p>
      </CardContent>
    </Card>
  );
}

function CreateCampaignDialog({ open, onOpenChange }: { open: boolean; onOpenChange: (open: boolean) => void }) {
  const { toast } = useToast();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      description: "",
      status: "draft",
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: FormData) => {
      return await apiRequest("POST", "/api/campaigns", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: "Campaign created",
        description: "Your new campaign has been created successfully.",
      });
      form.reset();
      onOpenChange(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create campaign.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: FormData) => {
    createMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent data-testid="dialog-create-campaign">
        <DialogHeader>
          <DialogTitle>Create New Campaign</DialogTitle>
        </DialogHeader>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Campaign Name</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Q1 2024 Outreach"
                      {...field}
                      data-testid="input-campaign-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description (Optional)</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe your campaign goals..."
                      {...field}
                      data-testid="input-campaign-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <DialogFooter>
              <Button type="submit" disabled={createMutation.isPending} data-testid="button-submit-campaign">
                {createMutation.isPending ? "Creating..." : "Create Campaign"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

export default function CampaignsPage() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);

  const { data: campaigns, isLoading } = useQuery<Campaign[]>({
    queryKey: ["/api/campaigns"],
  });

  return (
    <div className="flex h-screen flex-col bg-background">
      <header className="sticky top-0 z-50 flex h-16 items-center justify-between border-b px-6 backdrop-blur supports-[backdrop-filter]:bg-background/95">
        <div className="flex items-center gap-3">
          <Link href="/">
            <Button variant="ghost" size="icon" data-testid="button-back">
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <div className="flex h-9 w-9 items-center justify-center rounded-md bg-primary">
            <Phone className="h-5 w-5 text-primary-foreground" />
          </div>
          <div>
            <h1 className="text-lg font-semibold">Campaigns</h1>
            <p className="text-xs text-muted-foreground">Manage bulk calling campaigns</p>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="button-create-campaign">
            <Plus className="h-4 w-4 mr-2" />
            Create Campaign
          </Button>
          <ThemeToggle />
        </div>
      </header>

      <main className="flex-1 overflow-auto p-6">
        {isLoading ? (
          <div className="flex items-center justify-center h-64">
            <p className="text-muted-foreground">Loading campaigns...</p>
          </div>
        ) : campaigns && campaigns.length > 0 ? (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {campaigns.map((campaign) => (
              <CampaignCard
                key={campaign.id}
                campaign={campaign}
                onSelect={(id) => console.log("Selected", id)}
              />
            ))}
          </div>
        ) : (
          <div className="flex flex-col items-center justify-center h-64 gap-4">
            <p className="text-muted-foreground">No campaigns yet. Create your first campaign to get started.</p>
            <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="button-create-first-campaign">
              <Plus className="h-4 w-4 mr-2" />
              Create Campaign
            </Button>
          </div>
        )}
      </main>

      <CreateCampaignDialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen} />
    </div>
  );
}

server/google-voice-automation.ts.content:
import { chromium, type Browser, type Page } from 'playwright';

interface GoogleVoiceConfig {
  email: string;
  password: string;
}

class GoogleVoiceDialer {
  private browser: Browser | null = null;
  private page: Page | null = null;
  private isLoggedIn: boolean = false;
  private config: GoogleVoiceConfig;

  constructor(config: GoogleVoiceConfig) {
    this.config = config;
  }

  async initialize() {
    try {
      this.browser = await chromium.launch({
        headless: true,
        args: [
          '--no-sandbox',
          '--disable-setuid-sandbox',
          '--disable-dev-shm-usage',
          '--disable-blink-features=AutomationControlled'
        ]
      });

      this.page = await this.browser.newPage();

      // Set viewport and user agent to appear more human-like
      await this.page.setViewportSize({ width: 1280, height: 720 });
      await this.page.setExtraHTTPHeaders({
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/121.0.0.0 Safari/537.36'
      });

      console.log('Browser initialized successfully');
    } catch (error) {
      console.error('Failed to initialize browser:', error);
      throw error;
    }
  }

  async login() {
    if (!this.page) throw new Error('Browser not initialized');

    try {
      console.log('Navigating to Google Voice...');
      await this.page.goto('https://voice.google.com', { 
        waitUntil: 'networkidle',
        timeout: 60000 
      });

      // Check if already logged in
      const isAlreadyLoggedIn = await this.page.url().includes('voice.google.com/u/');
      if (isAlreadyLoggedIn) {
        console.log('Already logged in to Google Voice');
        this.isLoggedIn = true;
        return;
      }

      // Click sign in button if present
      const signInButton = await this.page.locator('text=Sign in').first();
      if (await signInButton.isVisible({ timeout: 5000 }).catch(() => false)) {
        await signInButton.click();
        await this.page.waitForTimeout(2000);
      }

      // Enter email
      console.log('Entering email...');
      const emailInput = this.page.locator('input[type="email"]');
      await emailInput.waitFor({ state: 'visible', timeout: 10000 });
      await emailInput.fill(this.config.email);
      await this.page.keyboard.press('Enter');
      await this.page.waitForTimeout(3000);

      // Enter password
      console.log('Entering password...');
      const passwordInput = this.page.locator('input[type="password"]');
      await passwordInput.waitFor({ state: 'visible', timeout: 10000 });
      await passwordInput.fill(this.config.password);
      await this.page.keyboard.press('Enter');

      // Wait for navigation to Google Voice
      await this.page.waitForURL('**/voice.google.com/**', { timeout: 30000 });

      console.log('Successfully logged in to Google Voice');
      this.isLoggedIn = true;
    } catch (error) {
      console.error('Login failed:', error);
      throw new Error('Failed to login to Google Voice');
    }
  }

  async dialNumber(phoneNumber: string): Promise<boolean> {
    if (!this.page) throw new Error('Browser not initialized');
    if (!this.isLoggedIn) await this.login();

    try {
      console.log(`Dialing number: ${phoneNumber}`);

      // Navigate to the dialer/messages tab
      await this.page.goto('https://voice.google.com/u/0/messages', {
        waitUntil: 'networkidle',
        timeout: 30000
      });

      // Click on "New conversation" or dial button
      const newConversationButton = this.page.locator('[aria-label*="New"], [data-test*="new"], button:has-text("New")').first();
      await newConversationButton.waitFor({ state: 'visible', timeout: 10000 });
      await newConversationButton.click();
      await this.page.waitForTimeout(1000);

      // Type the phone number in the search/dial field
      const dialInput = this.page.locator('input[aria-label*="phone"], input[placeholder*="phone"], input[type="tel"]').first();
      await dialInput.waitFor({ state: 'visible', timeout: 10000 });
      await dialInput.fill(phoneNumber);
      await this.page.waitForTimeout(1000);

      // Click the call button
      const callButton = this.page.locator('[aria-label*="Call"], button:has-text("Call"), [data-test*="call"]').first();
      await callButton.waitFor({ state: 'visible', timeout: 5000 });
      await callButton.click();

      console.log(`Successfully initiated call to ${phoneNumber}`);

      // Wait a moment for the call to connect
      await this.page.waitForTimeout(2000);

      return true;
    } catch (error) {
      console.error(`Failed to dial ${phoneNumber}:`, error);
      throw error;
    }
  }

  async hangup() {
    if (!this.page) return;

    try {
      // Look for hangup/end call button
      const hangupButton = this.page.locator('[aria-label*="Hang up"], [aria-label*="End call"], button:has-text("End")').first();
      if (await hangupButton.isVisible({ timeout: 2000 }).catch(() => false)) {
        await hangupButton.click();
        await this.page.waitForTimeout(1000);
      }
    } catch (error) {
      console.error('Failed to hangup:', error);
    }
  }

  async close() {
    if (this.browser) {
      await this.browser.close();
      this.browser = null;
      this.page = null;
      this.isLoggedIn = false;
      console.log('Browser closed');
    }
  }

  async screenshot(path: string) {
    if (this.page) {
      await this.page.screenshot({ path, fullPage: true });
      console.log(`Screenshot saved to ${path}`);
    }
  }
}

// Singleton instance
let dialerInstance: GoogleVoiceDialer | null = null;

export async function getDialer(): Promise<GoogleVoiceDialer> {
  const email = process.env.GOOGLE_VOICE_EMAIL;
  const password = process.env.GOOGLE_VOICE_PASSWORD;

  if (!email || !password) {
    throw new Error('Google Voice credentials not configured. Set GOOGLE_VOICE_EMAIL and GOOGLE_VOICE_PASSWORD environment variables.');
  }

  if (!dialerInstance) {
    dialerInstance = new GoogleVoiceDialer({ email, password });
    await dialerInstance.initialize();
    await dialerInstance.login();
  }

  return dialerInstance;
}

export async function closeDialer() {
  if (dialerInstance) {
    await dialerInstance.close();
    dialerInstance = null;
  }
}

export async function automatedDial(phoneNumber: string): Promise<boolean> {
  const dialer = await getDialer();
  return await dialer.dialNumber(phoneNumber);
}

